class MessagesComment < ActiveRecord::Base
   
  # ==========================================================================
  # Relationships
  # ==========================================================================
  belongs_to :user
  belongs_to :message

  
  # ==========================================================================
  # Validations
  # ==========================================================================
  before_save :apply_filter  

  # ==========================================================================
  # Attributes Accessors
  # ==========================================================================

  # ==========================================================================
  # Extra defnitions
  # ==========================================================================

  def apply_filter
    self.body_html = EnkiFormatter.format_as_xhtml(self.body)
  end
 

end
