class ToDoComment < ActiveRecord::Base
   
  # ==========================================================================
  # Relationships
  # ==========================================================================
  belongs_to :user
  belongs_to :to_do

  
  # ==========================================================================
  # Validations
  # ==========================================================================
  before_save :apply_filter  

  # ==========================================================================
  # Attributes Accessors
  # ==========================================================================

  attr_accessor :users

  # ==========================================================================
  # Extra defnitions
  # ==========================================================================

  def apply_filter
    self.body_html = TextFormatter.format_as_xhtml(self.body)
  end
 

end
