class ToDoList < ActiveRecord::Base
  # ==========================================================================
  # Relationships
  # ==========================================================================
  
  belongs_to :project
  has_many :to_dos

  # ==========================================================================
  # Validations
  # ==========================================================================



  # ==========================================================================
  # Extra defnitions
  # ==========================================================================



  # ==========================================================================
  # Instance Methods
  # ==========================================================================

  def divide_done_todos
    done    = []
    notDone = []
    self.to_dos.each do |todo|
      if todo.done?
        done << todo
      else
        notDone << todo
      end    
    end
    
    return {:done => done, :notDone => notDone}
  end  

  # ==========================================================================
  # Class Methods
  # ==========================================================================

  class << self
    
  end

end